import SkillScale from "./SkillScale"
import Product from "./Product"

function ShoppingList({cart, updateCart}) {

    const products = [
        {
            id: 1,
            name: 'Nokia Lumia',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            name: 'Samsung Galaxy',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            name: 'iPhone 11 Pro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img1.webp',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            name: 'MacBookPro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img4.webp',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ];

    return (
        <div className="container">
            <h1 className="mb-5">Produits</h1>
            <div className="row">
                products.map(({id, name, photo, battery, camera, dualSim, price}) => {
                    <div key={id}>
                        <Product name={name} photo={photo} battery={battery} camera={camera} dualSim={dualSim} price={price} />
                        <button onClick={() => updateCart(cart + 1)}>Ajouter</button>
                    </div>
                })
            </div>
        </div>
    );
}

export default ShoppingList;
