function SmartProducts() {

    const products = [
        {
            id: 1,
            name: 'Nokia Lumia',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            name: 'Samsung Galaxy',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            name: 'iPhone 12',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            name: 'Xiaomi',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ];




    const items = products.map(
        (product) => {
            return (
                <div className="row">
                    <div className="d-flex flex-column">
                        <div className="card smartshop__product align-self-end mt-2 mb-2" style={{width: '18rem'}}>
                            <div className="card-body">
                                <h4 className="card-title">{product.name}</h4>
                                <img className="card-img-top" src="..." alt="Card image cap" />
                                <p className="card-text">{product.battery}</p>
                                <p className="card-text">{product.camera}</p>
                                <p className="card-text">{product.dualSim}</p>
                                <p className="card-text">{product.price}</p>

                                <button href="#" className="btn btn-success" onClick={handleBuy, product.name}>Acheter</button>
                            </div>
                        </div>
                    </div>
                </div>
            )
        }
    )
    return (
        <>
        {items}
        </>
    )

    function handleBuy(e) {
        e.preventDefault();
        alert('Etes-vous sûr de rajouter' + products.name + 'à votre panier ?')
        //console.log('ça marche !') // OK
    }
}

export default SmartProducts
