// Bootstrap CSS
import "bootstrap/dist/css/bootstrap.min.css";
// Bootstrap Bundle JS
import "bootstrap/dist/js/bootstrap.bundle.min";
import { useState } from 'react'
import '../App.scss';
import '../sass/_main.scss'
import Banner from './Banner';
import Description from './Description';
import Cart from './Cart';
import ShoppingList from './ShoppingList';

function App() {

  const [cart, updateCart] = useState([])

  return (
    <body>


      <header className="mb-2" style={{height: '120px'}}>
        <div className="row">
            <img src="https://logo.com/image-cdn/images/kts928pd/production/a195406f1cbf3510e8901abf512267d4a80d2230-359x359.png?w=1080&q=72" style={{maxWidth: '8%', paddingLeft: '16px', marginTop: '8px'}} alt="logo" />
        </div>
      </header>

      <div className="container-fluid" style={{marginTop: '200px'}} > 
        <div className="row">
          <div className="col-lg-12">
            <Banner />
            <Description />
          </div>
        </div>

        <div className="row main">
          <div className="col-lg-8">
            <ShoppingList cart={cart} updateCart={updateCart} />
          </div>
          <div className="col-lg-4">
            <Cart cart={cart} updateCart={updateCart} />
          </div>
        </div>
      </div>

    </body>

  );
}

export default App
