import Cart from './Cart';

function SmartProducts({children}) {

    const products = [
        {
            id: 1,
            name: 'Nokia Lumia',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            name: 'Samsung Galaxy',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            name: 'iPhone 11 Pro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img1.webp',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            name: 'MacBookPro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img4.webp',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ];




    const items = products.map(
        (product) => {
            return (
                <>
                    <div>
                    {{children}}
                </div>
                <div key={product.id} className="col-lg-4">
                    <div className="card mb-3">
                        <div className="card-body">
                            <div class="row">
                                <div class="col-lg-6">
                                <img src={product.photo}
                                    className="img-fluid rounded-3" alt="Shopping item" style={{width: '100%'}} />
                                </div>
                                <div class="col-lg-6 mb-5">
                                    <div className="ms-3 mb-4">
                                        <h5>{product.name}</h5>
                                            <p className="small mb-0">
                                                {product.camera} pixels
                                            </p>

                                        <div className="ms-3">
                                            <p className="small mb-0">{product.battery} hrs</p>
                                        </div>

                                        <div>
                                            <p className="small mb-0">dualSim: {product.dualSim}</p>
                                        </div>

                                        <div className="mb-3" style={{verticalAlign: 'bottom'}}>
                                            <h5 className="mb-0">{product.price} â¬</h5>
                                        </div>
                                    </div>
                                </div>
                                <hr />
                                <div class="d-flex flex-column align-items-center">
                                    <button href="#" className="btn btn-success" style={{width: '40%'}} onClick={handleBuy}>Acheter</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                </>
            )
        }
    )
    return (
        <>
        {items}
        </>
    )

    function handleBuy(e) {
        e.preventDefault();
        // alert('Etes-vous sÃ»r de rajouter' + products.name + 'Ã  votre panier ?')
        return Cart();
        //console.log('Ã§a marche !') // OK
    }
}

export default SmartProducts
