import Cart from './Cart';

function SmartProducts() {

    const products = [
        {
            id: 1,
            name: 'Nokia Lumia',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            name: 'Samsung Galaxy',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            name: 'iPhone 12',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            name: 'Xiaomi',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ];




    const items = products.map(
        (product) => {
            return (


                <div class="col-lg-4">
                    <div class="card mb-3">
                        <div class="card-body">
                            <div class="">
                                <div class="">
                                    <div>
                                    <img
                                        src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img3.webp"
                                        class="img-fluid rounded-3" alt="Shopping item" style={{width: '65px'}} />
                                    </div>
                                    <div class="ms-3">
                                        <h5>{product.name}</h5>
                                            <p class="small mb-0">{product.camera} pixels</p>
                                        <div class="ms-3">
                                            <p class="small mb-0">{product.battery} hrs</p>
                                        </div>
                                        <div>
                                            <p class="small mb-0">dualSim: {product.dualSim}</p>
                                        </div>
                                        <div>
                                            <h5 class="mb-0">{product.price} €</h5>
                                        </div>
                                            <button href="#" className="btn btn-success" onClick={handleBuy}>Acheter</button>
                                        </div>
                                    </div>
                                    {/* <a href="#!" style={{color: 'cecece'}}><i class="fas fa-trash-alt"></i></a> */}

                            </div>
                        </div>
                    </div>
                </div>



                // <div className="row">
                //     <div className="d-flex flex-column">
                //         <div className="card smartshop__product align-self-end mt-2 mb-2" style={{width: '18rem'}}>
                //             <div className="card-body">
                //                 <h4 className="card-title">{product.name}</h4>
                //                 <img className="card-img-top" src="..." alt="Card image cap" />
                //                 <p className="card-text">{product.battery}</p>
                //                 <p className="card-text">{product.camera}</p>
                //                 <p className="card-text">{product.dualSim}</p>
                //                 <p className="card-text">{product.price}</p>


                //             </div>
                //         </div>
                //     </div>
                // </div>
            )
        }
    )
    return (
        <>
        {items}
        </>
    )

    function handleBuy(e) {
        e.preventDefault();
        // alert('Etes-vous sûr de rajouter' + products.name + 'à votre panier ?')
        return Cart();
        //console.log('ça marche !') // OK
    }
}

export default SmartProducts
