function SmartProducts() {

    const products = [
        {
            id: 1,
            name: 'Nokia Lumia',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            name: 'Samsung Galaxy',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            name: 'iPhone 12',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            name: 'Xiaomi',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ];


    const items = products.map(
        (product) => {
            return (
                <div className="smartshop__product text-capitalize">
                    <ul className="smartshop__details"> 
                        <li>
                            <span className="text-right">Nom : {product.name}</span>
                        </li>
                        <li>
                            <span>Dur√©e de la batterie : {product.battery}</span>
                        </li>
                        <li>
                            <span>Camera (en pixels) : {product.camera}</span>
                        </li>
                        <li>
                            <span>{product.dualSim}</span>
                        </li>
                        <li>
                            <span>{product.price}</span>
                        </li>
                    </ul>
                </div>
            )
        }
    )
    return (
        <>
        {items}
        </>
    )



}

export default SmartProducts
