    const items = products.map(
        (product) => {
            return (
                <div key={product.id} className="col-lg-4">
                    <div className="card mb-3">
                        <div className="card-body">
                            <div className="row">
                                <div className="col-lg-6">
                                <img src={product.photo}
                                    className="img-fluid rounded-3" alt="Shopping item" style={{width: '100%'}} />
                                </div>
                                <div className="col-lg-6 mb-5">
                                    <div className="ms-3 mb-4">
                                        <h5>{product.name}</h5>
                                            <p className="small mb-0">
                                                {product.camera} pixels
                                            </p>
                                        <div className="ms-3">
                                            <p className="small mb-0">{product.battery} hrs</p>
                                        </div>

                                        <div>
                                            <p className="small mb-0">dualSim: {product.dualSim}</p>
                                        </div>

                                        <div className="mb-3" style={{verticalAlign: 'bottom'}}>
                                            <h5 className="mb-0">{product.price} €</h5>
                                        </div>
                                    </div>
                                </div>
                                <hr />
                                <div className="d-flex flex-column align-items-center">
                                    <button href="#" className="btn btn-success" style={{width: '40%'}} onClick={() => updateCart(cart + 1)}>Ajouter au panier</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )

