import Product from './Product'
import Categories from './Categories'

function ShoppingList({ cart, updateCart }) {

    const products = [
        {
            id: 1,
            category: 'smartphone',
            name: 'Nokia Lumia',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            category: 'smartphone',
            name: 'Samsung Galaxy',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            category: 'smartphone',
            name: 'iPhone 11 Pro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img1.webp',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            category: 'pc',
            name: 'MacBookPro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img4.webp',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        },
        {
            id: 5,
            category: 'pc',
            name: 'MacBookPro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img4.webp',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        },
        {
            id: 6,
            category: 'pc',
            name: 'MacBookPro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img4.webp',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ]

    function addToCart(name, price) {
		const currentProductSaved = cart.find((product) => product.name === name)

		if (currentProductSaved) {
			const cartFilteredCurrentproduct = cart.filter(
				(product) => product.name !== name
			)
			updateCart([
				...cartFilteredCurrentproduct,
				{ name, price, amount: currentProductSaved.amount + 1 }
			])
		} else {
			updateCart([...cart, { name, price, amount: 1 }])
		}
	}

    return (
        <div className="container">
            <h1 className="mb-4">Catalogue</h1> 
            <div className="row">
                {products.map(({id, name, photo, battery, camera, dualSim, price}) => (
                    <div className="col-lg-4 mb-4">
                        <Product 
                            key={id}
                            name={name}
                            photo={photo}
                            battery={battery}
                            camera={camera}
                            dualSim={dualSim}
                            price={price} 
                        />

                        <div className="text-center">
                            <button className="btn btn-success" onClick={() => addToCart(name, price)}>Ajouter au panier</button>
                        </div>

                    </div>
                ))}
            </div>
        </div>
    );
}

export default ShoppingList;
