import Cart from './Cart';
import { useState } from 'react'

function SmartProducts() {

    const products = [
        {
            id: 1,
            name: 'Nokia Lumia',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 48,
            camera: 240,
            dualSim: 'yes',
            price: 450 
        },
        {
            id: 2,
            name: 'Samsung Galaxy',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img2.webp',
            battery: 60,
            camera: 180,
            dualSim: 'no',
            price: 500
        },
        {
            id: 3,
            name: 'iPhone 11 Pro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img1.webp',
            battery: 24,
            camera: 150,
            dualSim: 'no',
            price: 150 
        },
        {
            id: 4,
            name: 'MacBookPro',
            photo: 'https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-shopping-carts/img4.webp',
            battery: 70,
            camera: 250,
            dualSim: 'yes',
            price: 345
        }
    ];

    const [cart, updateCart] = useState(0)

    // iterate through product objects
    const items = products.map(
        (product) => {
            return (
                <div key={product.id} className="col-lg-4">
                    <div className="card mb-3">
                        <div className="card-body">
                            <div className="row">
                                <div className="col-lg-6">
                                <img src={product.photo}
                                    className="img-fluid rounded-3" alt="Shopping item" style={{width: '100%'}} />
                                </div>
                                <div className="col-lg-6 mb-5">
                                    <div className="ms-3 mb-4">
                                        <h5>{product.name}</h5>
                                            <p className="small mb-0">
                                                {product.camera} pixels
                                            </p>
                                        <div className="ms-3">
                                            <p className="small mb-0">{product.battery} hrs</p>
                                        </div>

                                        <div>
                                            <p className="small mb-0">dualSim: {product.dualSim}</p>
                                        </div>

                                        <div className="mb-3" style={{verticalAlign: 'bottom'}}>
                                            <h5 className="mb-0">{product.price} €</h5>
                                        </div>
                                    </div>
                                </div>
                                <hr />
                                <div class="d-flex flex-column align-items-center">
                                    <button href="#" className="btn btn-success" style={{width: '40%'}} onClick={() => updateCart(cart + 1)}>Ajouter au panier</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )

            function handleBuy() {
                //e.preventDefault();
                return alert('Etes-vous sûr de rajouter ' + product.name + ' à votre panier ?')
            }
        },
    )
    return (
        <>
        {items}
        </>
    )
}

export default SmartProducts
